require "debug"

data = DATA.read.chomp

# part 1
puts data.count("(") - data.count(")")

# part 2
current_floor = 0

data.each_char.with_index do |char, index|
  if char == "("
    current_floor += 1
  else
    current_floor -= 1
  end

  if current_floor == -1
    puts index + 1
    return
  end
end
__END__
